module DelayCircuit (
    input  wire clk,      // Clock signal
    input  wire rst,     // Reset signal
    input  wire data_in, // Input signal to be delayed
    output reg data_out  // Output signal after delay
);

    // Internal register to store the delayed data
    reg [7:0] delay_reg;

    // Always block to handle the clock edge
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            // Reset the register on reset
            delay_reg <= 8'b0;
            data_out <= 1'b0;
        end else begin
            // Shift the data into the register
            delay_reg <= {delay_reg[6:0], data_in};
            // Output the current value of the register
            data_out <= delay_reg[7];
        end
    end

endmodule

