    reg in;
    wire out;

    TopModule top_module (
        .in(in),
        .out(out)
    );

    initial begin
        $display("Simulation of TopModule starting...");

        // Test case 1: Input is 0, Output should be 0
        in = 0;
        #10; // Wait for 10 time units
        $display("Input: %b, Output: %b", in, out);

        // Test case 2: Input is 1, Output should be 1
        in = 1;
        #10; // Wait for 10 time units
        $display("Input: %b, Output: %b", in, out);

        // Test case 3: Input toggles between 0 and 1
        in = 0;
        #10; // Wait for 10 time units
        $display("Input: %b, Output: %b", in, out);
        in = 1;
        #10; // Wait for 10 time units
        $display("Input: %b, Output: %b", in, out);
        in = 0;
        #10; // Wait for 10 time units
        $display("Input: %b, Output: %b", in, out);

        $finish;
    end
endmodule

