        if (rst) begin
            out_hi <= 8'b0;
            out_lo <= 8'b0;
            counter <= 4'b0;
        end else begin
            out_hi <= in[15:8];
            out_lo <= in[7:0];
            counter <= counter + 1;
            if (counter == 15) begin
                counter <= 4'b0; // Wrap around after 15
