module TopModule (
    input  [N-1:0] in,
    output [N-1:0] out
);

    // Create an array to hold the reversed bits
    reg [N-1:0] reversed_bits;

    // Loop through each bit of the input
    generate
        for (genvar i = 0; i < N; i++) begin
            // Reverse the bit by shifting it to the correct position
            reversed_bits[i] = in[N-1-i];
        end
    endgenerate

    // Assign the reversed bits to the output
    assign out = reversed_bits;

endmodule

