module SimpleCounter(
    input clk,
    input rst,
    output reg count
);

reg [2:0] internal_count;

always @(posedge clk or posedge rst) begin
    if (rst) begin
        internal_count <= 3'b000;
    end else begin
        internal_count <= internal_count + 1'b1;
        if (internal_count == 3'b111) begin
            internal_count <= 3'b000;
        end
    end
end

assign count = internal_count;

endmodule

