    input  in [2:0], // 3-bit input vector
    output out [1:0] // 2-bit output vector representing population count
);

    wire [2:0] temp; // Temporary wire to hold intermediate results

    // Count the number of '1's in the input vector
    assign temp = in[2] + in[1] + in[0];

    // Assign the population count to the output vector
    assign out = temp[2:1];

endmodule

