module TopModule (
    input [31:0] in,
    input reset,
    output [31:0] out
);

    reg [31:0] temp;

    always @(posedge reset or posedge clk) begin
        if (reset) begin
            temp <= 0;
            out <= 0;
        end else begin
            temp = {in[7:0], in[15:8], in[23:16], in[31:24]};
            out <= temp;
        end
    end

endmodule

