module TopModule (
    input wire btn,
    output reg zero
);

always @(posedge btn or posedge rst) begin
    if (rst) begin
        zero <= 1'b0;
    end else begin
        if (btn == 1'b1) begin
            zero <= 1'b1;
        end else begin
            zero <= 1'b0;
        end
    end
end

endmodule

