module TopModule (
    input [63:0] in,
    output [63:0] out
);

    // Initialize an array to hold the reversed bytes
    reg [7:0] reversedBytes[7];

    // Reverse the byte order of the input
    always @(*) begin
        reversedBytes[0] = in[63:56];
        reversedBytes[1] = in[55:48];
        reversedBytes[2] = in[47:40];
        reversedBytes[3] = in[39:32];
        reversedBytes[4] = in[31:24];
        reversedBytes[5] = in[23:16];
        reversedBytes[6] = in[15:8];
        reversedBytes[7] = in[7:0];
    end

    // Combine the reversed bytes into the output
    assign out = {reversedBytes[7], reversedBytes[6], reversedBytes[5], reversedBytes[4], reversedBytes[3], reversedBytes[2], reversedBytes[1], reversedBytes[0]};

endmodule

