with_steering_implementation:
module TopModule_testbench;
    reg in1;
    reg in2;
    wire out;

    TopModule dut (
        .in1(in1),
        .in2(in2),
        .out(out)
    );

    initial begin
        // Test case 1: Both inputs are 0
        in1 = 0;
        in2 = 0;
        #10;
        $display("Test case 1: in1=0, in2=0, out=%b", out);
        assert (out == 1'b1) else $error("Test case 1 failed");

        // Test case 2: in1 is 0, in2 is 1
        in1 = 0;
        in2 = 1;
        #10;
        $display("Test case 2: in1=0, in2=1, out=%b", out);
        assert (out == 1'b1) else $error("Test case 2 failed");

        // Test case 3: in1 is 1, in2 is 0
        in1 = 1;
        in2 = 0;
        #10;
        $display("Test case 3: in1=1, in2=0, out=%b", out);
        assert (out == 1'b1) else $error("Test case 3 failed");

        // Test case 4: Both inputs are 1
        in1 = 1;
        in2 = 1;
        #10;
        $display("Test case 4: in1=1, in2=1, out=%b", out);
        assert (out == 1'b0) else $error("Test case 4 failed");

        $finish;
    end

endmodule

