module TopModule(
    input wire [7:0] in,
    output reg [7:0] out
);

    // Function to validate the input
    function automatic void validateInput(input wire [7:0] in);
        if (in < 0 || in > 255) begin
            $error("Invalid input value");
            $finish;
        end
    endfunction

    // Main module logic
    initial begin
        validateInput(in);
        out = ~in;
    end

endmodule

