// Testbench for TopModule
module TopModule_tb;

    // Declare inputs and outputs
    reg [15:0] in;
    wire [7:0] out_hi;
    wire [7:0] out_lo;

    // Instantiate TopModule
    TopModule dut (
        .in(in),
        .out_hi(out_hi),
        .out_lo(out_lo)
    );

    // Initial block to test the module
    initial begin
        // Test case 1: Input is 0x1234
        in = 16'h1234;
        #10; // Wait for 10 time units
        $display("Input: %h", in);
        $display("Output Hi: %h", out_hi);
        $display("Output Lo: %h", out_lo);
        assert(out_hi == 8'h34 && out_lo == 8'h12) else $error("Test case 1 failed");

        // Test case 2: Input is 0x5678
        in = 16'h5678;
        #10; // Wait for 10 time units
        $display("Input: %h", in);
        $display("Output Hi: %h", out_hi);
        $display("Output Lo: %h", out_lo);
        assert(out_hi == 8'h78 && out_lo == 8'h56) else $error("Test case 2 failed");

        // Test case 3: Input is 0x9ABC
        in = 16'h9ABC;
        #10; // Wait for 10 time units
        $display("Input: %h", in);
        $display("Output Hi: %h", out_hi);
        $display("Output Lo: %h", out_lo);
        assert(out_hi == 8'CBA && out_lo == 8'9A) else $error("Test case 3 failed");

        // Test case 4: Input is 0xDEAD
        in = 16'hDEAD;
        #10; // Wait for 10 time units
        $display("Input: %h", in);
        $display("Output Hi: %h", out_hi);
        $display("Output Lo: %h", out_lo);
        assert(out_hi == 8'DE && out_lo == 8xAD) else $error("Test case 4 failed");

        // Final assertion to check if all test cases passed
        assert($rose(out_hi) && $rose(out_lo)) else $error("All test cases passed");
    end

endmodule
