module TopModule_tb;

    // Inputs
    wire a;
    wire b;

    // Outputs
    wire out;

    // Instantiate the TopModule
    TopModule top_module (
        .a(a),
        .b(b),
        .out(out)
    );

    // Initial conditions
    initial begin
        a = 0;
        b = 0;
        #10; // Wait for 10 cycles
        a = 1;
        b = 0;
        #10; // Wait for 10 cycles
        a = 0;
        b = 1;
        #10; // Wait for 10 cycles
        a = 1;
        b = 1;
        #10; // Wait for 10 cycles
    end

    // Monitor the output
    always @(posedge out) begin
        $display("a=%b, b=%b, out=%b", a, b, out);
    end

endmodule

