module TopModule (
    input  clk,
    input  rst,
    input  [3:0] in_a,
    input  [3:0] in_b,
    output reg [7:0] out_sum
);

reg [3:0] reg_a;
reg [3:0] reg_b;

always @(posedge clk or posedge rst) begin
    if (rst) begin
        reg_a <= 4'b0;
        reg_b <= 4'b0;
        out_sum <= 8'b0;
    end else begin
        reg_a <= in_a;
        reg_b <= in_b;
        out_sum <= reg_a + reg_b;
    end
end

always @(*) begin
    if (rst) begin
        out_sum <= 8'b0;
    end else begin
        out_sum <= reg_a + reg_b;
    end
end

endmodule
