module LogicOperation(
    input [2:0] a,
    output [1:0] b
);

    // Calculate XOR between the first two inputs
    wire xor_result;
    xor_result = a[0] ^ a[1];

    // Calculate AND between the XOR result and the third input
    wire and_result;
    and_result = xor_result & a[2];

    // Assign the result of the AND operation to the output
    assign b = and_result;

endmodule

