module ToggleLED (
    input clk,
    input reset,
    output led
);

    reg [31:0] counter;

    always @(posedge clk or posedge reset) begin
        if (reset)
            counter <= 0;
        else
            counter <= counter + 1;
    end

    always @(posedge clk) begin
        if (counter == 5000000) begin // Assuming a 50 MHz clock
            led <= ~led;
            counter <= 0;
        end
    end

endmodule

