module TopModule (
    input  [2:0] in,
    output [1:0] out
);

    // Define internal signals
    wire [2:0] temp;
    wire [2:0] count;

    // Temporary signal to hold intermediate results
    assign temp = in & 1'b1;

    // Count the number of '1's in the input vector
    assign count = temp + (temp >> 1) + (temp >> 2);

    // Output the result
    assign out = count[1:0];

