module LogicBasedOutput (
    input wire [7:0] input_signal,
    output reg [3:0] output_signal
);

always @(*) begin
    if (input_signal < 50) begin
        output_signal = 4'b0000;
    end else if (input_signal <= 99) begin
        output_signal = 4'b0001;
    end else if (input_signal == 100) begin
        output_signal = 4'b0010;
    end else begin
        output_signal = 4'b0011;
    end
end

endmodule

