module TopModule (
    input [31:0] in,
    output [31:0] out
);

    // Create a temporary register to hold the reversed bytes
    reg [31:0] temp;

    // Iterate over each byte of the input
    for (genvar i = 0; i < 4; i++) begin
        // Extract the current byte from the input
        assign temp[i*8 +:8] = in[7 + i*8 : 0];
    end

    // Assign the reversed bytes to the output
    assign out = temp;

endmodule

