module Counter (
    input clk,
    input rst,
    output reg [3:0] count
);
    always @(posedge clk or posedge rst) begin
        if (rst)
            count <= 4'b0000;
        else if (count == 4'b1111)
            count <= 4'b0000;
        else
            count <= count + 1'b1;
    end
endmodule

