module TopModule (
    input [2:0] in,
    output reg [1:0] out
);

    // Declare variables to hold intermediate results
    reg [1:0] count0;
    reg [1:0] count1;
    reg [1:0] count2;

    // Implement the population count logic using a series of always blocks
    always @(*) begin
        // Initialize counters to zero
        count0 = 0;
        count1 = 0;
        count2 = 0;

        // Count the number of '1's in each position
        if (in[0]) count0 = 1;
        if (in[1]) count1 = 1;
        if (in[2]) count2 = 1;

        // Sum the counts to get the total population count
        out = count0 + count1 + count2;
    end

endmodule

