module TopModule_TestBench;

    // Declare the signals
    reg a;
    reg b;
    wire out;

    // Instantiate the TopModule
    TopModule uut (
        .a(a),
        .b(b),
        .out(out)
    );

    // Initial block to set up the test conditions
    initial begin
        // Test case 1: a = 0, b = 0
        a = 0;
        b = 0;
        #10; // Wait for 10 time units
        $display("Test case 1: a = %b, b = %b, out = %b", a, b, out);
        assert (out == 1'b1) else $error("Test case 1 failed");

        // Test case 2: a = 0, b = 1
        a = 0;
        b = 1;
        #10; // Wait for 10 time units
        $display("Test case 2: a = %b, b = %b, out = %b", a, b, out);
        assert (out == 1'b0) else $error("Test case 2 failed");

        // Test case 3: a = 1, b = 0
        a = 1;
        b = 0;
        #10; // Wait for 10 time units
        $display("Test case 3: a = %b, b = %b, out = %b", a, b, out);
        assert (out == 1'b0) else $error("Test case 3 failed");

        // Test case 4: a = 1, b = 1
        a = 1;
        b = 1;
        #10; // Wait for 10 time units
        $display("Test case 4: a = %b, b = %b, out = %b", a, b, out);
        assert (out == 1'b1) else $error("Test case 4 failed");

        // Finish the simulation
        $finish;
    end

endmodule

