module Adder_4bit (
    input [3:0] a,
    input [3:0] b,
    output reg [4:0] sum
);

    // Declare carry signals
    reg [4:0] carry;
    reg [4:0] sum_internal;

    // Adder core using full adders
    always @(*) begin
        sum_internal[4] = a[4] ^ b[4] ^ carry[4];
        sum_internal[3] = a[3] ^ b[3] ^ carry[3];
        sum_internal[2] = a[2] ^ b[2] ^ carry[2];
        sum_internal[1] = a[1] ^ b[1] ^ carry[1];
        sum_internal[0] = a[0] ^ b[0] ^ carry[0];

        carry[4] = (a[4] & b[4]) | (a[4] & carry[3]) | (b[4] & carry[3]);
        carry[3] = (a[3] & b[3]) | (a[3] & carry[2]) | (b[3] & carry[2]);
        carry[2] = (a[2] & b[2]) | (a[2] & carry[1]) | (b[2] & carry[1]);
        carry[1] = (a[1] & b[1]) | (a[1] & carry[0]) | (b[1] & carry[0]);
        carry[0] = (a[0] & b[0]);
    end

    // Assign the sum_internal to sum after shifting left by one bit
    assign sum = {carry[4], sum_internal};

endmodule

