    wire [31:0] xor_reversed;

    // Perform XOR operation on each byte
    assign xor_reversed = {
        in[7:0] ^ 0xFF,  // XOR the first byte with 0xFF
        in[15:8] ^ 0xFF, // XOR the second byte with 0xFF
        in[23:16] ^ 0xFF, // XOR the third byte with 0xFF
        in[31:24] ^ 0xFF  // XOR the fourth byte with 0xFF
    };

    // Reverse the XORed byte order
    assign reversed = {
        xor_reversed[7:0],     // Reverse the first byte
        xor_reversed[15:8],    // Reverse the second byte
        xor_reversed[23:16],   // Reverse the third byte
        xor_reversed[31:24]    // Reverse the fourth byte
    };

    // Assign the reversed byte order to the output port
    assign out = reversed;

endmodule

