module adder (
    input [1:0] a,
    input [1:0] b,
    output [1:0] sum
);

// Sum of two bits
sum[0] = a[0] ^ b[0]; // XOR gate for the least significant bit
sum[1] = a[1] & b[1]; // AND gate for the carry bit

// Carry generation
wire carry = a[0] & b[0]; // AND gate for the carry generation

// Full Adder
assign sum[1] = sum[1] | carry; // OR gate to combine the carry with the sum of the least significant bits

endmodule

