module TopModule (
    input wire clk,
    input wire reset,
    input wire [3:0] input_data,
    output reg [3:0] output_data
);

reg [3:0] counter;

always @(posedge clk or posedge reset) begin
    if (reset) begin
        counter <= 4'b0;
    end else begin
        counter <= counter + 1'b1;
        if (counter == 8) begin
            output_data <= counter;
        end
    end
end

endmodule

