module TopModule (
    input  [31:0] in,
    output [31:0] out
);

    // Create four 8-bit variables to hold each byte of the input
    reg [7:0] byte0;
    reg [7:0] byte1;
    reg [7:0] byte2;
    reg [7:0] byte3;

    // Assign each byte from the input to the respective register
    assign byte0 = in[7:0];
    assign byte1 = in[15:8];
    assign byte2 = in[23:16];
    assign byte3 = in[31:24];

    // Create four 8-bit variables to hold the reversed bytes
    reg [7:0] reversed_byte0;
    reg [7:0] reversed_byte1;
    reg [7:0] reversed_byte2;
    reg [7:0] reversed_byte3;

    // Assign each reversed byte to the respective register
    assign reversed_byte0 = byte3;
    assign reversed_byte1 = byte2;
    assign reversed_byte2 = byte1;
    assign reversed_byte3 = byte0;

    // Combine the reversed bytes into a single 32-bit output
    assign out = {reversed_byte3, reversed_byte2, reversed_byte1, reversed_byte0};

endmodule

