module TopModule_tb;

    // Instantiate the TopModule
    TopModule top_module (
        .in(1'b0),   // Input signal
        .out(out)     // Output signal
    );

    // Create a wire to capture the output signal
    wire out;

    // Initial block to set up the simulation environment
    initial begin
        // Set the input signal to 0
        top_module.in <= 1'b0;

        // Wait for 10 time units to allow the simulation to settle
        #10;

        // Check the output signal
        if (out == 1'b1) begin
            $display("Test passed: Output is 1 when input is 0");
        end else begin
            $display("Test failed: Output is not 1 when input is 0");
        end

        // Set the input signal to 1
        top_module.in <= 1'b1;

        // Wait for 10 time units to allow the simulation to settle
        #10;

        // Check the output signal
        if (out == 1'b0) begin
            $display("Test passed: Output is 0 when input is 1");
        end else begin
            $display("Test failed: Output is not 0 when input is 1");
        end

        // Finish the simulation
        $finish;
    end

endmodule

