module TopModule(
    input  [N-1:0] in,
    output [M-1:0] out
);

    // Internal signals
    wire [N-1:0] temp;
    wire [M-1:0] count;

    // Population count logic
    always @(*) begin
        temp = in; // Copy input to temporary variable
        count = 0; // Initialize count to zero

        // Count number of '1's in the input vector
        for (int i = 0; i < N; i++) begin
            if (temp[i]) count = count + 1;
        end
    end

    // Assign output
    assign out = count;

endmodule

