module TopModule (
    input wire [31:0] in,
    output wire [31:0] out
);

    // Create a temporary register to hold the reversed bytes
    reg [31:0] temp;

    // Loop through each byte of the input
    for (genvar i = 0; i < 4; i++) begin
        // Shift the current byte to the correct position in the temp register
        temp[i*8 +:8] = in[(i+1)*8 - 1:i*8];
    end

    // Check if the input is a valid 32-bit number
    always @(posedge in) begin
        if (in != 32'hFFFFFFFF) begin
            $display("Invalid input: %h", in);
        end
    end

    // Assign the reversed bytes to the output
    assign out = temp;

endmodule

