module top_level_design (
    input clk,
    input rst,
    input [31:0] data_in,
    output reg [31:0] data_out
);

    TopModule top_module_inst (
        .in(data_in[0]), // Connect the least significant bit of data_in to the in port of TopModule
        .out(data_out[0])  // Connect the least significant bit of data_out to the out port of TopModule
    );

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            data_out <= 0;
        end else begin
            data_out <= data_in; // Copy the entire data_in to data_out
        end
    end

endmodule

