module TopModule(
    input  in[2:0],
    output out[2:0]
);

    // Count the number of '1's in the input vector
    reg [1:0] count;

    always @(*) begin
        count = 0;
        for (int i = 0; i < 3; i++) begin
            if (in[i]) begin
                count = count + 1;
            end
        end
    end

    // Check if the number of '1's is greater than or equal to 2
    reg check;

    always @(*) begin
        check = 0;
        if (count >= 2) begin
            check = 1;
        end
    end

    // Assign the count and check values to the out port
    assign out[2] = check;
    assign out[1:0] = count;

endmodule

