    reg a;
    reg b;

    // Outputs
    wire out;

    // Instantiate the TopModule instance
    TopModule dut (
        .a(a),
        .b(b),
        .out(out)
    );

    initial begin
        // Test case 1: a=0, b=0
        a = 0;
        b = 0;
        #10; // Wait for 10 time units
        assert (out == 0) else $error("Test case 1 failed");

        // Test case 2: a=0, b=1
        a = 0;
        b = 1;
        #10; // Wait for 10 time units
        assert (out == 0) else $error("Test case 2 failed");

        // Test case 3: a=1, b=0
        a = 1;
        b = 0;
        #10; // Wait for 10 time units
        assert (out == 0) else $error("Test case 3 failed");

        // Test case 4: a=1, b=1
        a = 1;
        b = 1;
        #10; // Wait for 10 time units
        assert (out == 1) else $error("Test case 4 failed");

        $finish;
    end

endmodule

