module DelayModule (
    input wire in,
    output reg out,
    parameter DELAY_CYCLES = 10 // Default delay is 10 cycles
);

always @(posedge clk) begin
    if (reset) begin
        out <= 0;
    end else if (delay_cycles > 0) begin
        out <= in;
        delay_cycles <= delay_cycles - 1;
    end else begin
        out <= 0;
    end
end

initial begin
    delay_cycles <= DELAY_CYCLES;
end

endmodule

