module TopModule (
    input wire [31:0] in,
    output reg [31:0] out
);

    always @(*) begin
        // Extract each byte from the input
        byte0 = in[7:0];
        byte1 = in[15:8];
        byte2 = in[23:16];
        byte3 = in[31:24];

        // Combine the bytes into the output in reversed order
        out = {byte3, byte2, byte1, byte0};
    end

endmodule

