module SimpleCounter (
    input wire clk,
    input wire rst,
    output reg count,
    output reg next_count
);
    // Reset the counter to zero on rising edge of reset
    always @(posedge rst) begin
        count <= 0;
    end
    // Increment the counter on rising edge of clock
    always @(posedge clk) begin
        count <= count + 1;
        next_count <= count + 1;
    end
endmodule

