module TopModule_testbench;

    // Instantiate the TopModule
    TopModule top_module (
        .in1(1'b0),
        .in2(1'b0),
        .out(out)
    );

    // Clock signal
    reg clk;
    initial begin
        clk = 1'b0;
        forever #5 clk = ~clk;
    end

    // Reset signal
    reg reset;
    initial begin
        reset = 1'b1;
        #10 reset = 1'b0;
    end

    // Testbench logic
    always @(posedge clk or posedge reset) begin
        if (reset == 1'b1) begin
            out <= 1'b0;
        end else begin
            case ({in1, in2})
                2'b00: out <= 1'b1; // NOR of 00 is 1
                2'b01: out <= 1'b1; // NOR of 01 is 1
                2'b10: out <= 1'b1; // NOR of 10 is 1
                2'b11: out <= 1'b0; // NOR of 11 is 0
            endcase
        end
    end

    // Output port
    wire out;

endmodule

