module TwoBitAdder (
    input [1:0] a, // Input A is a 2-bit vector
    input [1:0] b, // Input B is a 2-bit vector
    output [1:0] sum, // Sum of A and B is a 2-bit vector
    output carry // Carry out of the addition
);

// Define signals for carry propagation
wire c1, c2;

// First level of full adder
full_adder #(.WIDTH(1)) fa1 (
    .a(a[1]),
    .b(b[1]),
    .c(c1),
    .sum(sum[1])
);

// Second level of full adder
full_adder #(.WIDTH(1)) fa2 (
    .a(a[0]),
    .b(b[0]),
    .c(c1),
    .sum(sum[0]),
    .c(c2)
);

// Output the carry
assign carry = c2;

endmodule

// Define the full adder module
module full_adder (
    input a,
    input b,
    input c,
    output sum,
    output c
);

// Logic for full adder
assign sum = (a ^ b) ^ c;
assign c = (a & b) | (b & c) | (a & c);

endmodule

