module TopModule (
    input a,
    input b,
    input clk,
    input rst,
    output out
);

    // Implement an XNOR gate
    reg xnor_out;
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            xnor_out <= 0;
        end else begin
            xnor_out <= ~(a ^ b);
        end
    end

    assign out = xnor_out;

endmodule

