module TopModule (
    input [31:0] in,
    output [31:0] out
);

    // Define intermediate signals to hold each byte
    reg [7:0] b0, b1, b2, b3;

    // Assign each byte from the input to the corresponding intermediate signal
    assign b0 = in[7:0];
    assign b1 = in[15:8];
    assign b2 = in[23:16];
    assign b3 = in[31:24];

    // Combine the bytes in reverse order
    assign out = {b3, b2, b1, b0};

endmodule

