module TopModule (
    input a,
    input b,
    input control,
    output out
);

    reg and_mode;

    // Initialize the AND mode based on the control signal
    always @(posedge control) begin
        if (control)
            and_mode <= 1'b1; // Set AND mode
        else
            and_mode <= 1'b0; // Set NAND mode
    end

    // Assign the output based on the current AND mode
    assign out = and_mode ? a & b : ~a & ~b;

endmodule

