module TopModule (
    input [2:0] in,
    output [1:0] out
);

    // Initialize the count to 0
    reg [1:0] count = 2'b0;

    // Iterate through each bit of the input
    generate
        for (genvar i = 0; i < 3; i++) begin : bit_counter
            always @(in[i]) begin
                if (in[i]) begin
                    count = count + 1;
                end
            end
        end
    endgenerate

    // Assign the count to the output
    assign out = count;

