    input [2:0] in,
    output [1:0] out
);

    // Declare intermediate signals
    wire [2:0] temp;
    wire [1:0] count;

    // Implement population count circuit using bitwise operations
    assign temp = in; // Copy input to intermediate signal
    assign count = (temp & 1) + (temp >> 1 & 1) + (temp >> 2 & 1); // Count '1's

    // Assign output to the count signal
    assign out = count;

endmodule

