module RisingEdgeDelay (
    input wire clk,      // Clock input
    input wire reset,    // Reset input
    input wire signal_in, // Input signal to detect rising edge
    output reg signal_out // Output signal delayed by one clock cycle
);

    // Flip-flop to detect rising edge
    reg prev_signal;
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            prev_signal <= 0;
            signal_out <= 0;
        end else begin
            prev_signal <= signal_in;
            if (signal_in && !prev_signal) begin
                signal_out <= 1;
            end
        end
    end

endmodule
