    reg a;
    reg b;
    initial begin
        $display("Running Testbench...");
        repeat (100) begin // Run 100 test cases
            a = $random; // Randomize input 'a'
            b = $random; // Randomize input 'b'
            #5; // Wait for 5 cycles
            // Compute the expected output using the XNOR logic
            expected_out = ~(a ^ b);
            // Compare the expected output with the actual output from the TopModule
            if (expected_out == out) begin
                $display("Test Passed: a = %b, b = %b, out = %b", a, b, out);
            end else begin
                $display("Test Failed: a = %b, b = %b, expected_out = %b, out = %b", a, b, expected_out, out);
            end
        end
        $finish();
    end

    // Assign a default value to 'out' to avoid uninitialized warnings
    wire out;

endmodule

