module TopModule (
    input a,
    input b,
    output out
);

    // Define the AND gate using three logic gates: NAND, NOT, and OR
    // 1. NAND gate: Outputs LOW when both inputs are HIGH
    // 2. NOT gate: Inverts the input (outputs LOW when input is HIGH and vice versa)
    // 3. OR gate: Outputs HIGH when at least one input is HIGH

    // Create a temporary variable to store the result of the NAND gate
    wire temp;

    // Connect the first input 'a' to the first input of the NAND gate
    assign temp = ~a & ~b; // ~(~a & ~b) = a & b

    // Connect the result of the NAND gate to the second input of the OR gate
    assign out = temp | ~temp; // (a & b) | (~a & ~b) = a & b

endmodule

