module TopModule (
    input wire clk,
    input wire rst_n,
    input [2:0] in,
    output reg [1:0] out
);

    // Internal signals
    wire [2:0] temp;
    wire [1:0] count;

    // Population count logic
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            temp <= 3'b0;
            count <= 2'b0;
        end else begin
            temp <= in;
            count <= temp[2] + temp[1] + temp[0];
        end
    end

    // Output assignment
    assign out = count;

endmodule

