module AdderSubtractor (
    input [3:0] A, // 4-bit input A
    input [3:0] B, // 4-bit input B
    input [3:0] Cin, // 1-bit carry-in
    output [3:0] Sum, // 4-bit sum
    output Cout // 1-bit carry-out
);

    wire [3:0] Sum1, Sum2; // Intermediate sums
    wire [3:0] Carry1, Carry2; // Intermediate carries

    // First stage of the adder/subtractor
    Sum1 <= A ^ B; // XOR operation for sum
    Carry1 <= A & B; // AND operation for carry

    // Second stage of the adder/subtractor
    Sum2 <= Sum1 ^ Cin; // XOR operation with carry-in
    Carry2 <= Sum1 & Cin; // AND operation with carry-in

    // Final sum and carry-out
    Sum <= Sum2;
    Cout <= Carry2;

endmodule

