with_steering_implementation:
module top_module (
    input clk,
    input rst,
    input [3:0] data_in,
    output [3:0] data_out
);

    // Wire to connect two modules
    wire wire_data;

    // Register to store the current state of the FSM
    reg [1:0] fsm_state;

    // Connect the wire to the data_in port of module A
    assign wire_data = data_in;

    // Implement the FSM using registers
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            fsm_state <= 2'b00;
        end else begin
            case (fsm_state)
                2'b00: fsm_state <= 2'b01;
                2'b01: fsm_state <= 2'b10;
                2'b10: fsm_state <= 2'b11;
                2'b11: fsm_state <= 2'b00;
            endcase
        end
    end

    // Connect the register to the data_out port of module B
    assign data_out = fsm_state;

endmodule

