// Full Adder module
module full_adder (
    input a, // First input
    input b, // Second input
    input cin, // Carry input
    output sum, // Sum output
    output cout // Carry output
);

    // Assignments for full adder logic
    assign sum = (a & b) | (a & cin) | (b & cin);
    assign cout = (a & b) | (a & cin) | (b & cin);

endmodule

